mapper.select:
SELECT COUNT(1) AS NUM , XZQH AS CODE, (SELECT NAME FROM SYS_AREA A WHERE A.CODE=XZQH) AS NAME FROM TB_ST_AJDJXX AJ WHERE 1=1
AND DATE_FORMAT(AJ.DJSJ,'%Y-%m-%d')>=#{filter.djsjBegin,jdbcType=VARCHAR}
AND DATE_FORMAT(AJ.DJSJ,'%Y-%m-%d')<=#{filter.djsjEnd,jdbcType=VARCHAR}
GROUP BY AJ.XZQH

SELECT DQDW AS CODE, (SELECT NAME FROM SYS_ORGAN SO WHERE SO.CODE=DQDW) AS NAME, COUNT(1) AS NUM FROM ( SELECT AJBH, DQDW FROM TB_ST_AJLZXX WHERE 1=1
AND DATE_FORMAT(LZSJ,'%Y-%m-%d')>=#{filter.djsjBegin,jdbcType=VARCHAR}
AND DATE_FORMAT(LZSJ,'%Y-%m-%d')<=#{filter.djsjEnd,jdbcType=VARCHAR}
GROUP BY AJBH,DQDW ) GZL GROUP BY DQDW

SELECT COUNT(1) AS NUM, (SELECT SA.NAME FROM SYS_AREA SA WHERE T.SZDD LIKE CONCAT('%',SA.NAME,'%') AND SA.PAR_CODE ='100000' LIMIT 0,1) AS NAME FROM TB_ST_SJZQ T WHERE 1=1
AND T.SZSJ>=#{filter.szsjBegin,jdbcType=VARCHAR}
AND T.SZSJ<=#{filter.szsjEnd,jdbcType=VARCHAR}
GROUP BY (SELECT SA.NAME FROM SYS_AREA SA WHERE T.SZDD LIKE CONCAT('%',SA.NAME,'%') LIMIT 0,1) ORDER BY COUNT(1) DESC
